function signUpClick(){let login=$("input.login-signup").val(),email=$("input.email-signup").val(),password=$("input.password-signup").val(),passwordVerify=$("input.password-verify-signup").val();nonEmpty(login,email,password,passwordVerify)&&loginValid(login)&&passwordValid(password)&&emailValid(email)&&passwordValidCheck(password,passwordVerify)&&(console.log("All valid, start request to signin..."),$.ajax({url:url+"/signin",type:"POST",dataType:"json",data:{login,email,password},success:function(answer){console.log(answer)},error:function(){console.log("404!"),errorShow("Невдалось підєндатись до сервера!")}}))}function signInClick(){let login=$("input.login-signin").val(),password=$("input.password-signin").val();return login.length?password.length?void(loginValid(login)&&passwordValid(password)&&(console.log("All valid, start request to signup..."),$.ajax({url:url+"/signup",type:"POST",dataType:"json",data:{login,password},success:function(answer){console.log(answer)},error:function(){console.log("404!"),errorShow("Невдалось підєндатись до сервера!")}}))):(console.error("Empty password!"),errorShow("Ви не ввели пароль!"),!1):(console.error("Empty login!"),errorShow("Ви не ввели логін!"),!1)}function errorShow(error){alert(error)}function nonEmpty(login,email,password,passwordVerify){if(login.length){if(email.length){if(password.length){if(passwordVerify.length)return!0;console.error("Empty password verify!");return errorShow("Ви не ввели повторний пароль!"),!1}console.error("Empty password!");return errorShow("Ви не ввели пароль!"),!1}console.error("Empty email!");return errorShow("Ви не ввели email!"),!1}console.error("Empty login!");return errorShow("Ви не ввели логін!"),!1}function loginValid(login){if(pattern=/^[a-zA-Z](.[a-zA-Z0-9_-]*)$/,login.length>10){console.error("Login lenght > 10!");return errorShow("Логін повинен містити не більше 10 символів"),!1}if(login.length<4){console.error("Login lenght < 4!");return errorShow("Логін повинен містити не менше 4 символів"),!1}if(pattern.test(login))return!0;console.error("Login invalid!");return errorShow("Логін містить недопустимі символи!"),!1}function passwordValid(password){if(password.length>16){console.error("Password lenght > 16!");return errorShow("Пароль повинен містити не більше 16 символів"),!1}if(password.length<4){console.error("password lenght < 4!");return errorShow("Пароль повинен містити не менше 4 символів"),!1}return!0}function emailValid(email){if(email.length<4){console.error("Email lenght < 4!");return errorShow("Надто короткий email"),!1}return!0}function passwordValidCheck(password,passwordVerify){if(password!==passwordVerify){console.error("Password != passwordVerify!");return errorShow("Паролі не збігаються!"),!1}return!0}let url="www.google.com";